import './App.css';
import React, {  useState } from 'react';
import ImageLinkForm from './components/imageLinkForm/ImageLinkForm';
import Logo from './components/logo/Logo';
import Navigation from './components/navigation/Navigation';
import Rank from './components/rank/Rank';
import FaceRecognition from './components/faceRecognition/FaceRecognition';
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import SignIn from './components/signIn/SignIn';
import Register from './components/register/Register';



function App() {
  const [input, setInput] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [boxCoordinates, setBoxCoordinates] = useState({});
  const [route, setRoute] = useState('signin');
  const [isSignedIn, setIsSignedIn] = useState(false)


   const particlesOptions = {
    fpsLimit: 120,
    interactivity: {
      events: {
        onHover: {
          enable: true,
          mode: "repulse",
        },
        resize: true,
      },
      modes: {
        push: {
          quantity: 4,
        },
        repulse: {
          distance: 100,
          duration: 0.4,
        },
      },
    },
    particles: {
      color: {
        value: "#ffffff",
      },
      links: {
        color: "#ffffff",
        distance: 150,
        enable: true,
        opacity: 0.5,
        width: 1,
      },
      collisions: {
        enable: true,
      },
      move: {
        direction: "none",
        enable: true,
        outModes: {
          default: "bounce",
        },
        random: false,
        speed: 2,
        straight: false,
      },
      number: {
        density: {
          enable: true,
          area: 900,
        },
        value: 80,
      },
      opacity: {
        value: 0.5,
      },
      shape: {
        type: "circle",
      },
      size: {
        value: { min: 1, max: 5 },
      },
    },
    detectRetina: true,
  }

  const calculateBoxCoordinates = (data) => {
    const faceCoordinates = JSON.parse(data, null, 2).outputs[0].data.regions[0].region_info.bounding_box;
    console.log(faceCoordinates)
    const image = document.getElementById('inputImage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftColumn: faceCoordinates.left_col * width,
      topRow: faceCoordinates.top_row * height,
      rightColumn: width - (faceCoordinates.right_col * width),
      bottomRow: height - (faceCoordinates.bottom_row * height)
    }
  }

  const displayFaceBox = (boxCoordinates) => {
    console.log(boxCoordinates)
    setBoxCoordinates(boxCoordinates);
  }

  const onInputChange = (e) => {
    console.log(input)
    setInput(e.target.value)
  }

  const onSubmit = () => {
    setImageUrl(input);
    const raw = JSON.stringify({
      "user_app_id": {
            "user_id": "ltbrp03abi48",
            "app_id": "f232d0bac678457aae0377bd2cf8cd13"
        },
      "inputs": [
        {
          "data": {
            "image": {
              "url": `${input}`
            }
          }
        }
      ]
    });

    const requestOptions = {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Authorization': 'Key 93c052416161441e9a3db9c5122a28a0'
      },
      body: raw
    };

    fetch("https://api.clarifai.com/v2/models/face-detection/outputs", requestOptions)
      .then(response => response.text())
      .then(result => displayFaceBox(calculateBoxCoordinates(result)))
      .catch(error => console.log('error', error));
  }

  const onRouteChange = (route) => {
    if (route === 'signout') {
      setIsSignedIn(false)
    } else if (route === 'home') {
      setIsSignedIn(true)
    }
    setRoute(route);
  }

  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <div className="App">
      <Navigation isSignedIn={isSignedIn} onRouteChange={onRouteChange} />
      { route === 'home' 
        ? <div>
            <Logo />
            <Rank />
            <ImageLinkForm 
              onInputChange={onInputChange} 
              onSubmit={onSubmit}/>
            <FaceRecognition boxCoordinates={boxCoordinates} imageUrl={imageUrl} />
            </div>
        : ( route === 'signin'
        ? <SignIn onRouteChange={onRouteChange} />
        : <Register onRouteChange={onRouteChange} />
        )
      }

      {/* <Particles
        id="tsparticles"
        init={particlesInit}
        options={particlesOptions}
    /> */}
    </div>
  );
}

export default App;
